import {AnimatePresence, m} from 'framer-motion';
import SlideContent from './SlideContent';
import {useState} from 'react';
import {createPortal} from 'react-dom';

export default function SlideOne({slideIndex, slidePos}) {
  const [modalState, setModalState] = useState(true);

  const status = slideIndex === slidePos && !modalState ? 'animate' : 'initial';

  return (
    <SlideContent>
      <m.div
        animate={status}
        className='bg-red-100 relative flex justify-center items-center p-4'
        style={{height: `calc(100vh - 32px)`, width: `calc(100vw - 32px)`}}
      >
        <AnimatePresence>
          {modalState && <Modal control={setModalState} />}
        </AnimatePresence>

        <h1 className='uppercase font-tajawal font-black text-6xl relative z-10 sm:text-8xl'>
          Happy Eid al-Fitr!
        </h1>

        <div className='absolute bg-yellow-200 overflow-hidden top-0 h-full w-full'>
          <m.img
            initial={{
              scale: 1.3,
            }}
            variants={{
              initial: {
                scale: 1.3,
              },
              animate: {
                scale: 1.1,
                transition: {
                  delay: 0.7,
                  duration: 1,
                  ease: [0.32, 0.72, 0, 1],
                },
              },
            }}
            className='h-full w-full object-cover'
            src='/slidebg.webp'
            alt=''
          />
        </div>

        <div className='absolute top-8 left-0'>
          <IconSpark />
        </div>
        <div className='absolute bottom-8 right-0'>
          <IconSpark />
        </div>
        <div className='hidden md:block absolute top-64 right-32 scale-50 z-20'>
          <IconSpark />
        </div>
      </m.div>
    </SlideContent>
  );
}

