function Modal({control}) {
  const [lang, setLang] = useState('id');

  function changeLang() {
    setLang((prevLang) => (prevLang === 'eng' ? 'id' : 'eng'));
  }

  return createPortal(
    <m.div
      initial={{
        top: '100%',
        x: '-50%',
      }}
      animate={{
        top: '25%',
        x: '-50%',
        rotate: 5,
        rotateY: [180, 0],
        transition: {
          rotateY: {
            duration: 0.4,
          },
        },
      }}
      exit={{
        top: '100%',
        rotateY: [0, 180],
        transition: {
          rotateY: {
            duration: 0.4,
          },
        },
      }}
      id='message'
      className='p-4 flex flex-col  w-80 h-fit absolute left-1/2 -translate-y-1/2 rounded-2xl z-50 bg-amber-50'
    >
      <button
        className='bg-amber-200 h-10 w-10 text-xs rounded-xl self-end mb-2'
        onClick={changeLang}
      >
        {lang === 'eng' ? 'eng' : 'id'}
      </button>

      <p className='flex flex-col gap-2 mb-10'>
        <span className='font-bold text-lg'>Instruction!</span>
        {lang === 'eng' ? (
          <>
            <span>
              Click the left and right sides of your device screen to move
              slides.
            </span>
            <span>
              Or if you are using a PC you can also use the arrow keys to
              navigate
            </span>
          </>
        ) : (
          <>
            <span>
              Klik sisi kiri dan kanan dari layar device anda untuk berpindah
              slide.
            </span>
            <span>
              Atau jika anda menggunakan PC anda juga bisa menggunakan tombol
              panah untuk navigasi
            </span>
          </>
        )}
        <span className='mt-16'>Sincerely,</span>
        <span>Saepul Bahri</span>
      </p>

      <button
        className='bg-slate-950 text-white py-2 px-8 rounded-full font-tajawal uppercase shadow-lg'
        onClick={() => control(false)}
      >
        Close
      </button>
    </m.div>,
    document.getElementById('modal')
  );
}
